import React, { useState } from "react";

const App = () => {
    const [completeName, setCompleteName] = useState({
        fname : '',
        lname : '',
        email : '',
        phone : ''
    });

    const inputElement = (event) => {
        const name = event.target.name;
        const value = event.target.value

        //const {value, name} = event.target;

        setCompleteName((preValue) => {
            console.log(preValue);
            return {
                ...preValue,
                [name] : value, 
            }

        })
    }

    const onSubmits = (event) => {
        event.preventDefault();
    }

    return (
        <>  
            <form onSubmit={onSubmits}>
                <div>
                    <h1>Hello {completeName.fname} {completeName.lname}</h1>
                    <input type="text" placeholder="Enter Your Name" name="fname" onChange={inputElement} value={completeName.fname} />

                    <input type="text" placeholder="Enter Your Last Name" name="lname" onChange={inputElement} value={completeName.lname} />

                    <input type="email" placeholder="Enter Your Email" name="email" onChange={inputElement} value={completeName.email} />

                    <input type="number" placeholder="Enter Your Phone" name="phone" onChange={inputElement} value={completeName.phone} />                   
                    <button type="submit">Click Me</button>
                </div>
            </form>
        </>

    );

}

export default App;
