import React, { useState } from "react";

const App = () => {
    //let count = 1; 

    const [count, setCount] = useState(0);
    //console.log(state);

    const IncNum = () => {
        setCount(count + 1);
        //console.log("clicked " + count++);
}

    return(
        <>
            <h1>{count}</h1>
            <button onClick={IncNum}> Click Me</button>
        </>
    );
};

export default App;
