import React, { useState } from "react";

const App = () => {
    let newTime = new Date().toLocaleTimeString();

    const[currTime, setTime] = useState(newTime);

    const uTime = () => {
         newTime = new Date().toLocaleTimeString();
        setTime(newTime);
    }

    setInterval(uTime, 1000);
    
    return (
        <>
            <h1>{currTime}</h1>
        </>
    );
}

export default App;
