import React, { useState } from "react";

const App = () => {
    const [fName, setName] = useState();

    const [lName, setFName] = useState();

    const inputElement = (event) => {
        setName(event.target.value);
    }

    const inputElements = (event) => {
        setFName(event.target.value);
    }

    const onSubmits = (event) => {
        event.preventDefault();
        setFName(fName);
    }

    return (
        <>  
            <form onSubmit={onSubmits}>
                <div>
                    <h1>Hello {lName}</h1>
                    <input type="text" placeholder="Enter Your Name" onChange={inputElement} />
                    <input type="text" placeholder="Enter Your Last Name" onChange={inputElements} />
                    <button type="submit">Click Me</button>
                </div>
            </form>
        </>

    );

}

export default App;
